mapper.select:
SELECT ITEM_NUM
FROM ITEM
WHERE NAME = #{itemName}

mapper.insert:
	INSERT INTO ITEM (
    ITEM_NUM, NAME, PRICE, STOCK, IMAGE_PATH, 
    ITEM_DETAIL, REG_DATE, MOD_DATE, BRAND )
	VALUES (ITEM_SEQ.NEXTVAL, #{name }, #{price }, #{stock }, #{imagePath },
	    #{itemDetail }, SYSDATE, SYSDATE, #{brand }
	)
